{"command": "write-file", "file_path": "components/TodoList.tsx", "content": "import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../store/store';
import { toggleAll } from '../store/todosSlice';
import { TodoItem } from './TodoItem';
import { ACTIVE_TODOS, COMPLETED_TODOS } from '../constants';
import { ITodo } from '../types';

interface TodoListProps {
  filter: string;
}

export const TodoList: React.FC<TodoListProps> = ({ filter }) => {
  const dispatch = useDispatch();
  const todos = useSelector((state: RootState) => state.todos.items);
  const editingId = useSelector((state: RootState) => state.todos.editingId);

  const filteredTodos = todos.filter((todo: ITodo) => {
    switch (filter) {
      case ACTIVE_TODOS:
        return !todo.completed;
      case COMPLETED_TODOS:
        return todo.completed;
      default:
        return true;
    }
  });

  const activeTodoCount = todos.filter(todo => !todo.completed).length;
  const allCompleted = todos.length > 0 && activeTodoCount === 0;

  const handleToggleAll = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(toggleAll(e.target.checked));
  };

  if (todos.length === 0) {
    return null;
  }

  return (
    <section className="main">
      <input
        id="toggle-all"
        className="toggle-all"
        type="checkbox"
        onChange={handleToggleAll}
        checked={allCompleted}
      />
      <label htmlFor="toggle-all">
        Mark all as complete
      </label>
      <ul className="todo-list">
        {filteredTodos.map((todo: ITodo) => (
          <TodoItem
            key={todo.id}
            todo={todo}
            editing={editingId === todo.id}
          />
        ))}
      </ul>
    </section>
  );
};"}

{"command": "write-file", "file_path": "components/TodoFooter.tsx", "content": "import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link, useLocation } from 'react-router-dom';
import { RootState } from '../store/store';
import { clearCompleted } from '../store/todosSlice';
import { ALL_TODOS, ACTIVE_TODOS, COMPLETED_TODOS } from '../constants';
import { pluralize } from '../utils';

export const TodoFooter: React.FC = () => {
  const dispatch = useDispatch();
  const location = useLocation();
  const todos = useSelector((state: RootState) => state.todos.items);

  const activeTodoCount = todos.filter(todo => !todo.completed).length;
  const completedCount = todos.length - activeTodoCount;

  if (todos.length === 0) {
    return null;
  }

  const activeTodoWord = pluralize(activeTodoCount, 'item');

  const nowShowing = location.pathname === '/active' ? ACTIVE_TODOS :
                     location.pathname === '/completed' ? COMPLETED_TODOS :
                     ALL_TODOS;

  const handleClearCompleted = () => {
    dispatch(clearCompleted());
  };

  return (
    <footer className="footer">
      <span className="todo-count">
        <strong>{activeTodoCount}</strong> {activeTodoWord} left
      </span>
      <ul className="filters">
        <li>
          <Link
            to="/"
            className={nowShowing === ALL_TODOS ? 'selected' : ''}>
            All
          </Link>
        </li>
        {' '}
        <li>
          <Link
            to="/active"
            className={nowShowing === ACTIVE_TODOS ? 'selected' : ''}>
            Active
          </Link>
        </li>
        {' '}
        <li>
          <Link
            to="/completed"
            className={nowShowing === COMPLETED_TODOS ? 'selected' : ''}>
            Completed
          </Link>
        </li>
      </ul>
      {completedCount > 0 && (
        <button
          className="clear-completed"
          onClick={handleClearCompleted}>
          Clear completed
        </button>
      )}
    </footer>
  );
};"}

{"command": "write-file", "file_path": "components/TodoItem.tsx", "content": "import React, { useState, useRef, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { toggleTodo, deleteTodo, updateTodo, setEditingId } from '../store/todosSlice';
import { ENTER_KEY, ESCAPE_KEY } from '../constants';
import { ITodoItemProps } from '../types';

export const TodoItem: React.FC<ITodoItemProps> = ({ todo, editing }) => {
  const dispatch = useDispatch();
  const [editText, setEditText] = useState(todo.title);
  const editFieldRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (editing && editFieldRef.current) {
      editFieldRef.current.focus();
      editFieldRef.current.setSelectionRange(
        editFieldRef.current.value.length,
        editFieldRef.current.value.length
      );
    }
  }, [editing]);

  const handleToggle = () => {
    dispatch(toggleTodo(todo.id));
  };

  const handleDestroy = () => {
    dispatch(deleteTodo(todo.id));
  };

  const handleEdit = () => {
    dispatch(setEditingId(todo.id));
    setEditText(todo.title);
  };

  const handleSubmit = () => {
    const val = editText.trim();
    if (val) {
      dispatch(updateTodo({ id: todo.id, title: val }));
      dispatch(setEditingId(null));
      setEditText(val);
    } else {
      handleDestroy();
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditText(e.target.value);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.keyCode === ESCAPE_KEY) {
      setEditText(todo.title);
      dispatch(setEditingId(null));
    } else if (e.keyCode === ENTER_KEY) {
      handleSubmit();
    }
  };

  return (
    <li className={`${todo.completed ? 'completed' : ''} ${editing ? 'editing' : ''}`}>
      <div className="view">
        <input
          className="toggle"
          type="checkbox"
          checked={todo.completed}
          onChange={handleToggle}
        />
        <label onDoubleClick={handleEdit}>
          {todo.title}
        </label>
        <button className="destroy" onClick={handleDestroy} />
      </div>
      <input
        ref={editFieldRef}
        className="edit"
        value={editText}
        onBlur={handleSubmit}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
      />
    </li>
  );
};"}

{"command": "write-file", "file_path": "components/TodoHeader.tsx", "content": "import React, { useState } from 'react';
