{"command": "write-file", "file_path": "components/TodoList.tsx", "content": "import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';
import { RootState } from '../store';
import { toggleAll } from '../todoSlice';
import { ALL_TODOS, ACTIVE_TODOS, COMPLETED_TODOS } from '../constants';
import TodoItem from './TodoItem';

function TodoList() {
  const dispatch = useDispatch();
  const location = useLocation();
  const todos = useSelector((state: RootState) => state.todos.todos);
  const editingId = useSelector((state: RootState) => state.todos.editingId);

  const getFilterType = () => {
    switch (location.pathname) {
      case '/active':
        return ACTIVE_TODOS;
      case '/completed':
        return COMPLETED_TODOS;
      default:
        return ALL_TODOS;
    }
  };

  const filterType = getFilterType();

  const shownTodos = todos.filter((todo) => {
    switch (filterType) {
      case ACTIVE_TODOS:
        return !todo.completed;
      case COMPLETED_TODOS:
        return todo.completed;
      default:
        return true;
    }
  });

  const activeTodoCount = todos.reduce((accum, todo) => {
    return todo.completed ? accum : accum + 1;
  }, 0);

  const handleToggleAll = (event: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(toggleAll(event.target.checked));
  };

  if (todos.length === 0) {
    return null;
  }

  return (
    <section className="main">
      <input
        id="toggle-all"
        className="toggle-all"
        type="checkbox"
        onChange={handleToggleAll}
        checked={activeTodoCount === 0}
      />
      <label htmlFor="toggle-all">
        Mark all as complete
      </label>
      <ul className="todo-list">
        {shownTodos.map((todo) => (
          <TodoItem
            key={todo.id}
            todo={todo}
            editing={editingId === todo.id}
          />
        ))}
      </ul>
    </section>
  );
}

export default TodoList;"}

{"command": "write-file", "file_path": "components/TodoHeader.tsx", "content": "import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addTodo } from '../todoSlice';
import { ENTER_KEY } from '../constants';

function TodoHeader() {
  const dispatch = useDispatch();
  const [inputValue, setInputValue] = useState('');

  const handleKeyDown = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.keyCode !== ENTER_KEY) {
      return;
    }

    event.preventDefault();

    const val = inputValue.trim();
    if (val) {
      dispatch(addTodo(val));
      setInputValue('');
    }
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
  };

  return (
    <header className="header">
      <h1>todos</h1>
      <input
        className="new-todo"
        placeholder="What needs to be done?"
        value={inputValue}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
        autoFocus
      />
    </header>
  );
}

export default TodoHeader;"}

{"command": "write-file", "file_path": "components/TodoFooter.tsx", "content": "import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation, Link } from 'react-router-dom';
import { RootState } from '../store';
import { clearCompleted } from '../todoSlice';
import { ALL_TODOS, ACTIVE_TODOS, COMPLETED_TODOS } from '../constants';
import { pluralize } from '../utils';

function TodoFooter() {
  const dispatch = useDispatch();
  const location = useLocation();
  const todos = useSelector((state: RootState) => state.todos.todos);

  const activeTodoCount = todos.reduce((accum, todo) => {
    return todo.completed ? accum : accum + 1;
  }, 0);

  const completedCount = todos.length - activeTodoCount;

  const getFilterType = () => {
    switch (location.pathname) {
      case '/active':
        return ACTIVE_TODOS;
      case '/completed':
        return COMPLETED_TODOS;
      default:
        return ALL_TODOS;
    }
  };

  const nowShowing = getFilterType();
  const activeTodoWord = pluralize(activeTodoCount, 'item');

  const handleClearCompleted = () => {
    dispatch(clearCompleted());
  };

  if (activeTodoCount === 0 && completedCount === 0) {
    return null;
  }

  return (
    <footer className="footer">
      <span className="todo-count">
        <strong>{activeTodoCount}</strong> {activeTodoWord} left
      </span>
      <ul className="filters">
        <li>
          <Link
            to="/"
            className={nowShowing === ALL_TODOS ? 'selected' : ''}
          >
            All
          </Link>
        </li>
        {' '}
        <li>
          <Link
            to="/active"
            className={nowShowing === ACTIVE_TODOS ? 'selected' : ''}
          >
            Active
          </Link>
        </li>
        {' '}
        <li>
          <Link
            to="/completed"
            className={nowShowing === COMPLETED_TODOS ? 'selected' : ''}
          >
            Completed
          </Link>
        </li>
      </ul>
      {completedCount > 0 && (
        <button
          className="clear-completed"
          onClick={handleClearCompleted}
        >
          Clear completed
        </button>
      )}
    </footer>
  );
}

export default TodoFooter;"}

{"command": "write-file", "file_path": "components/TodoItem.tsx", "content": "import React, { useState, useEffect, useRef, memo } from 'react';
import { useDispatch } from 'react-redux';
import { Todo } from '../types';
import { toggleTodo, destroyTodo, editTodo, saveTodo, cancelEdit } from '../todoSlice';
import { ENTER_KEY, ESCAPE_KEY } from '../constants';

interface TodoItemProps {
  todo: Todo;
  editing: boolean;
}

const TodoItem = memo(({ todo, editing }: TodoItemProps) => {
  const dispatch = useDispatch();
  const [editText, setEditText] = useState(todo.title);
  const editFieldRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (editing && editFieldRef.current) {
      const node = editFieldRef.current;
      node.focus();
      node.setSelectionRange(node.value.length, node.value.length);
    }
  }, [editing]);

  const handleSubmit = () => {
    const val = editText.trim();
    if (val) {
      dispatch(saveTodo({ id: todo.id, title: val }));
      setEditText(val);
    } else {
      dispatch(destroyTodo(todo.id));
    }
  };

  const handleEdit = () => {
    dispatch(editTodo(todo.id));
    setEditText(todo.title);
  };

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.keyCode === ESCAPE_KEY) {
      setEditText(todo.title);
      dispatch(cancelEdit());
    } else if (event.keyCode === ENTER_KEY) {
      handleSubmit();
    }
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setEditText(event.target.value);
  };

  const handleToggle = () => {
    dispatch(toggleTodo(todo.id));
  };

  const handleDestroy = () => {
    dispatch(destroyTodo(todo.id));
  };

  const itemClasses = [
    todo.completed ? 'completed' : '',
    editing ? 'editing' : ''
  ].filter(Boolean).join(' ');

  return (
    <li className={itemClasses}>
      <div className="view">
        <input
          className="toggle"
          type="checkbox"
          checked={todo.completed}
          onChange={handleToggle}
        />
        <label onDoubleClick={handleEdit}>
          {todo.title}
        </label>
        <button className="destroy" onClick={handleDestroy} />
      </div>
      <input
        ref={editFieldRef}
        className="edit"
        value={editText}
        onBlur={handleSubmit}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
      />
    </li>
  );
});

export default TodoItem;"}

