interface TodoInputProps {
  onAdd: (title: string) => void;
}

export const TodoInput: FC<TodoInputProps> = ({ onAdd }) => {
  const [text, setText] = useState('');

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    setText(e.target.value);
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      const value = text.trim();
      if (value) {
        onAdd(value);
        setText('');
      }
    }
  };

  return (
    <input
      className="new-todo"
      placeholder="What needs to be done?"
      value={text}
      onChange={handleChange}
      onKeyDown={handleKeyDown}
      autoFocus
    />
  );
};"}

{"command": "write-file", "file_path": "src/features/todos/TodosPage.tsx", "content": "import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { TodoInput } from './TodoInput';
import { TodoList } from './TodoList';
import { TodoFooter } from './TodoFooter';
import { addTodo, toggleAll, toggleTodo, deleteTodo, editTodo, saveTodo, cancelEdit, clearCompleted, setFilter } from './todosSlice';
import { Filter } from './types';
import { RootState } from '../../app/store';

export const TodosPage: React.FC = () => {
  const dispatch = useDispatch();
  const todos = useSelector((state: RootState) => state.todos.todos);
  const filter = useSelector((state: RootState) => state.todos.filter);
  const editingId = useSelector((state: RootState) => state.todos.editingId);
  const params = useParams<{ filter?: string }>();

  useEffect(() => {
    const filterParam = params.filter === 'active' || params.filter === 'completed' ? params.filter as Filter : 'all';
    dispatch(setFilter(filterParam));
  }, [params.filter, dispatch]);

  const filteredTodos = todos.filter(todo => {
    switch (filter) {
      case 'active':
        return !todo.completed;
      case 'completed':
        return todo.completed;
      default:
        return true;
    }
  });

  const activeCount = todos.reduce((count, todo) => (!todo.completed ? count + 1 : count), 0);
  const completedCount = todos.length - activeCount;

  return (
    <>
      <header className="header">
        <h1>todos</h1>
        <TodoInput onAdd={title => dispatch(addTodo(title))} />
      </header>
      <TodoList
        todos={filteredTodos}
        editingId={editingId}
        onToggleAll={checked => dispatch(toggleAll(checked))}
        onToggle={id => dispatch(toggleTodo(id))}
        onDestroy={id => dispatch(deleteTodo(id))}
        onEdit={id => dispatch(editTodo(id))}
        onSave={(id, text) => dispatch(saveTodo({ id, text }))}
        onCancel={() => dispatch(cancelEdit())}
      />
      <TodoFooter
        count={activeCount}
        completedCount={completedCount}
        filter={filter}
        onClearCompleted={() => dispatch(clearCompleted())}
      />
    </>
  );
};"}

{"command": "write-file", "file_path": "src/features/todos/todosSlice.ts", "content": "import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { nanoid } from 'nanoid';
import { Todo, Filter } from './types';

interface TodosState {
  todos: Todo[];
  filter: Filter;
  editingId: string | null;
}

const initialState: TodosState = {
  todos: [],
  filter: 'all',
  editingId: null,
};

const todosSlice = createSlice({
  name: 'todos',
  initialState,
  reducers: {
    addTodo: {
      reducer(state, action: PayloadAction<Todo>) {
        state.todos.push(action.payload);
      },
      prepare(title: string) {
        return { payload: { id: nanoid(), title, completed: false } };
      },
    },
    toggleAll(state, action: PayloadAction<boolean>) {
      state.todos.forEach((todo) => {
        todo.completed = action.payload;
      });
    },
    toggleTodo(state, action: PayloadAction<string>) {
      const todo = state.todos.find((t) => t.id === action.payload);
      if (todo) {
        todo.completed = !todo.completed;
      }
    },
    deleteTodo(state, action: PayloadAction<string>) {
      state.todos = state.todos.filter((t) => t.id !== action.payload);
    },
    editTodo(state, action: PayloadAction<string>) {
      state.editingId = action.payload;
    },
    saveTodo(state, action: PayloadAction<{ id: string; text: string }>) {
      const todo = state.todos.find((t) => t.id === action.payload.id);
      if (todo) {
        todo.title = action.payload.text;
      }
      state.editingId = null;
    },
    cancelEdit(state) {
      state.editingId = null;
    },
    clearCompleted(state) {
      state.todos = state.todos.filter((t) => !t.completed);
    },
    setFilter(state, action: PayloadAction<Filter>) {
      state.filter = action.payload;
    },
  },
});

export const {
  addTodo,
  toggleAll,
  toggleTodo,
  deleteTodo,
  editTodo,
  saveTodo,
  cancelEdit,
  clearCompleted,
  setFilter,
} = todosSlice.actions;

export default todosSlice.reducer;
"}

