{"command": "write-file", "file_path": "src/store/todoSlice.ts", "content": "import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { nanoid } from 'nanoid';
import { getTodos, saveTodos } from '../utils/storage';

type Todo = {
  id: string;
  title: string;
  completed: boolean;
};

const initialState: Todo[] = getTodos();

export const todoSlice = createSlice({
  name: 'todos',
  initialState,
  reducers: {
    addTodo: (state, action: PayloadAction<string>) => {
      const newTodo = {
        id: nanoid(),
        title: action.payload,
        completed: false
      };
      state.push(newTodo);
      saveTodos(state);
    },
    toggleTodo: (state, action: PayloadAction<string>) => {
      const todo = state.find(todo => todo.id === action.payload);
      if (todo) {
        todo.completed = !todo.completed;
        saveTodos(state);
      }
    },
    editTodo: (state, action: PayloadAction<{id: string, title: string}>) => {
      const todo = state.find(todo => todo.id === action.payload.id);
      if (todo) {
        todo.title = action.payload.title;
        saveTodos(state);
      }
    },
    removeTodo: (state, action: PayloadAction<string>) => {
      const index = state.findIndex(todo => todo.id === action.payload);
      if (index !== -1) {
        state.splice(index, 1);
        saveTodos(state);
      }
    },
    clearCompleted: (state) => {
      const activeTodos = state.filter(todo => !todo.completed);
      saveTodos(activeTodos);
      return activeTodos;
    },
    toggleAll: (state, action: PayloadAction<boolean>) => {
      state.forEach(todo => todo.completed = action.payload);
      saveTodos(state);
    }
  }
});

export const { addTodo, toggleTodo, editTodo, removeTodo, clearCompleted, toggleAll } = todoSlice.actions;
export default todoSlice.reducer;"}

{"command": "write-file", "file_path": "src/hooks/useTodoEscape.ts", "content": "import { useEffect } from 'react';

export const useTodoEscape = (onEscape: () => void) => {
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        onEscape();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [onEscape]);
};"}

{"command": "write-file", "file_path": "src/hooks/useTodoFocus.ts", "content": "import { useEffect, useRef } from 'react';

export const useTodoFocus = (shouldFocus: boolean) => {
  const ref = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (shouldFocus && ref.current) {
      ref.current.focus();
    }
  }, [shouldFocus]);

  return ref;
};"}

{"command": "write-file", "file_path": "src/components/Footer.tsx", "content": "import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store/store';
import { clearCompleted } from '../store/todoSlice';
import { Link, useLocation } from 'react-router-dom';

export const Footer: React.FC = () => {
  const todos = useSelector((state: RootState) => state.todos);
  const remainingCount = todos.filter(todo => !todo.completed).length;
  const location = useLocation();
  const dispatch = useDispatch();

  if (todos.length === 0) return null;

  return (
    <footer className="footer">
      <span className="todo-count">
        <strong>{remainingCount}</strong> {remainingCount === 1 ? 'item' : 'items'} left
      </span>
      <ul className="filters">
        <li>
          <Link to="/" className={location.pathname === '/' ? 'selected' : ''}>All</Link>
        </li>
        <li>
          <Link to="/active" className={location.pathname === '/active' ? 'selected' : ''}>Active</Link>
        </li>
        <li>
          <Link to="/completed" className={location.pathname === '/completed' ? 'selected' : ''}>Completed</Link>
        </li>
      </ul>
      {remainingCount < todos.length && (
        <button className="clear-completed" onClick={() => dispatch(clearCompleted())}>
          Clear completed
        </button>
      )}
    </footer>
  );
};"}

{"command": "write-file", "file_path": "src/components/TodoList.tsx", "content": "import React from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../store/store';
import TodoItem from './TodoItem';

type TodoListProps = {
  filter?: 'all' | 'active' | 'completed';
};

const TodoList: React.FC<TodoListProps> = ({ filter = 'all' }) => {
  const todos = useSelector((state: RootState) => {
    switch (filter) {
      case 'active':
        return state.todos.filter(todo => !todo.completed);
      case 'completed':
        return state.todos.filter(todo => todo.completed);
      default:
        return state.todos;
    }
  });

  if (todos.length === 0) return null;

  return (
    <section className="main">
      <ul className="todo-list">
        {todos.map(todo => (
          <TodoItem key={todo.id} todo={todo} />
        ))}
      </ul>
    </section>
  );
};

export default TodoList;"}

{"command": "write-file", "file_path": "src/components/TodoItem.tsx", "content": "import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { toggleTodo, editTodo, removeTodo } from '../store/todoSlice';
import { useTodoFocus } from '../hooks/useTodoFocus';
import { useTodoEscape } from '../hooks/useTodoEscape';

type TodoItemProps = {
  todo: {
    id: string;
    title: string;
    completed: boolean;
  };
};

const TodoItem: React.FC<TodoItemProps> = ({ todo }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(todo.title);
  const dispatch = useDispatch();
  const editInputRef = useTodoFocus(isEditing);

  const handleEdit = () => {
    setIsEditing(true);
    setEditTitle(todo.title);
  };

  const handleSubmit = () => {
    const trimmedTitle = editTitle.trim();
    if (trimmedTitle) {
      dispatch(editTodo({ id: todo.id, title: trimmedTitle }));
    } else {
      dispatch(removeTodo(todo.id));
    }
    setIsEditing(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  const handleEscape = () => {
    setEditTitle(todo.title);
    setIsEditing(false);
  };

  useTodoEscape(handleEscape);

  return (
    <li className={`${todo.completed ? 'completed' : ''} ${isEditing ? 'editing' : ''}`}>
      <div className="view">
        <input
          className="toggle"
          type="checkbox"
          checked={todo.completed}
          onChange={() => dispatch(toggleTodo(todo.id))}
        />
        <label onDoubleClick={handleEdit}>{todo.title}</label>
        <button className="destroy" onClick={() => dispatch(removeTodo(todo.id))} />
      </div>
      {isEditing && (
        <input
          ref={editInputRef}
          className="edit"
          value={editTitle}
          onChange={(e) => setEditTitle(e.target.value)}
          onBlur={handleSubmit}
          onKeyDown={handleKeyDown}
        />
      )}
    </li>
  );
};

export default TodoItem;"}

{"command": "write-file", "file_path": "src/components/TodoApp.tsx", "content": "import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store/store';
import { addTodo, toggleAll } from '../store/todoSlice';
import TodoList from './TodoList';
import Footer from './Footer';

export const TodoApp: React.FC = () => {
  const [newTodo, setNewTodo] = useState('');
  const todos = useSelector((state: RootState) => state.todos);
  const dispatch = useDispatch();
  const allCompleted = todos.length > 0 && todos.every(todo => todo.completed);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const trimmedTodo = newTodo.trim();
    if (trimmedTodo) {
      dispatch(addTodo(trimmedTodo));
      setNewTodo('');
    }
  };

  return (
    <div className="todoapp">
      <header className="header">
        <h1>todos</h1>
        <form onSubmit={handleSubmit} className="todo-form">
          <input
            className="new-todo"
            placeholder="What needs to be done?"
            value={newTodo}
            onChange={(e) => setNewTodo(e.target.value)}
            autoFocus
          />
        </form>
      </header>
      {todos.length > 0 && (
        <>
          <section className="main">
            <input
              id="toggle-all"
              className="toggle-all"
              type="checkbox"
              checked={allCompleted}
              onChange={() => dispatch(toggleAll(!allCompleted))}
            />
            <label htmlFor="toggle-all">Mark all as complete</label>
            <TodoList />
          </section>
          <Footer />
        </>
      )}
    </div>
  );
};"}

