{"command": "write-file", "file_path": "src/features/todos/TodoItem.tsx", "content": "import React, { useEffect, useRef, useState } from "react";
import classNames from "classnames";
import { ENTER_KEY, ESCAPE_KEY } from "./constants";

export interface Todo {
  id: string;
  title: string;
  completed: boolean;
}

interface TodoItemProps {
  todo: Todo;
  editing: boolean;
  onToggle: () => void;
  onDestroy: () => void;
  onEdit: () => void;
  onSave: (text: string) => void;
  onCancel: () => void;
}

export const TodoItem: React.FC<TodoItemProps> = ({
  todo,
  editing,
  onToggle,
  onDestroy,
  onEdit,
  onSave,
  onCancel,
}) => {
  const [editText, setEditText] = useState(todo.title);
  const editFieldRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (editing && editFieldRef.current) {
      editFieldRef.current.focus();
      editFieldRef.current.setSelectionRange(
        editFieldRef.current.value.length,
        editFieldRef.current.value.length
      );
    }
  }, [editing]);

  useEffect(() => {
    setEditText(todo.title);
  }, [todo.title]);

  const handleSubmit = (e: React.FormEvent) => {
    const val = editText.trim();
    if (val) {
      onSave(val);
      setEditText(val);
    } else {
      onDestroy();
    }
  };

  const handleEdit = () => {
    onEdit();
    setEditText(todo.title);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.keyCode === ESCAPE_KEY) {
      setEditText(todo.title);
      onCancel();
    } else if (e.keyCode === ENTER_KEY) {
      handleSubmit(e);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditText(e.target.value);
  };

  return (
    <li
      className={classNames({
        completed: todo.completed,
        editing: editing,
      })}
    >
      <div className="view">
        <input
          className="toggle"
          type="checkbox"
          checked={todo.completed}
          onChange={onToggle}
        />
        <label onDoubleClick={handleEdit}>{todo.title}</label>
        <button className="destroy" onClick={onDestroy} />
      </div>
      <input
        ref={editFieldRef}
        className="edit"
        value={editText}
        onBlur={handleSubmit}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
      />
    </li>
  );
};
"}

{"command": "write-file", "file_path": "src/features/todos/TodoApp.tsx", "content": "import React, { useRef, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../../store";
import {
  addTodo,
  toggleAllTodos,
  toggleTodo,
  destroyTodo,
  editTodo,
  saveTodo,
  cancelEdit,
  clearCompletedTodos,
  setNowShowing,
  selectTodos,
  selectNowShowing,
  selectEditingId,
} from "./todosSlice";
import { TodoItem } from "./TodoItem";
import { TodoFooter } from "./TodoFooter";
import { ALL_TODOS, ACTIVE_TODOS, COMPLETED_TODOS, ENTER_KEY } from "./constants";

export const TodoApp: React.FC = () => {
  const dispatch = useDispatch();
  const todos = useSelector(selectTodos);
  const nowShowing = useSelector(selectNowShowing);
  const editingId = useSelector(selectEditingId);
  const [inputValue, setInputValue] = useState("");
  const inputRef = useRef<HTMLInputElement>(null);

  React.useEffect(() => {
    const onHashChange = () => {
      switch (window.location.hash.replace("#", "")) {
        case "/active":
          dispatch(setNowShowing(ACTIVE_TODOS));
          break;
        case "/completed":
          dispatch(setNowShowing(COMPLETED_TODOS));
          break;
        default:
          dispatch(setNowShowing(ALL_TODOS));
      }
    };
    window.addEventListener("hashchange", onHashChange);
    onHashChange();
    return () => window.removeEventListener("hashchange", onHashChange);
  }, [dispatch]);

  const handleNewTodoKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.keyCode !== ENTER_KEY) return;
    e.preventDefault();
    const val = inputValue.trim();
    if (val) {
      dispatch(addTodo(val));
      setInputValue("");
    }
  };

  const handleToggleAll = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(toggleAllTodos(e.target.checked));
  };

  const shownTodos = todos.filter((todo) => {
    switch (nowShowing) {
      case ACTIVE_TODOS:
        return !todo.completed;
      case COMPLETED_TODOS:
        return todo.completed;
      default:
        return true;
    }
  });

  const activeTodoCount = todos.reduce((acc, todo) => (!todo.completed ? acc + 1 : acc), 0);
  const completedCount = todos.length - activeTodoCount;

  return (
    <div>
      <header className="header">
        <h1>todos</h1>
        <input
          ref={inputRef}
          className="new-todo"
          placeholder="What needs to be done?"
          value={inputValue}
          onChange={e => setInputValue(e.target.value)}
          onKeyDown={handleNewTodoKeyDown}
          autoFocus
        />
      </header>
      {todos.length > 0 && (
        <section className="main">
          <input
            id="toggle-all"
            className="toggle-all"
            type="checkbox"
            onChange={handleToggleAll}
            checked={activeTodoCount === 0}
          />
          <label htmlFor="toggle-all">Mark all as complete</label>
          <ul className="todo-list">
            {shownTodos.map((todo) => (
              <TodoItem
                key={todo.id}
                todo={todo}
                editing={editingId === todo.id}
                onToggle={() => dispatch(toggleTodo(todo.id))}
                onDestroy={() => dispatch(destroyTodo(todo.id))}
                onEdit={() => dispatch(editTodo(todo.id))}
                onSave={(text) => dispatch(saveTodo({ id: todo.id, title: text }))}
                onCancel={() => dispatch(cancelEdit())}
              />
            ))}
          </ul>
        </section>
      )}
      {(activeTodoCount > 0 || completedCount > 0) && (
        <TodoFooter
          count={activeTodoCount}
          completedCount={completedCount}
          nowShowing={nowShowing}
          onClearCompleted={() => dispatch(clearCompletedTodos())}
        />
      )}
    </div>
  );
};
"}

{"command": "write-file", "file_path": "src/features/todos/todosSlice.ts", "content": "import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { nanoid } from "nanoid";
import { ALL_TODOS } from "./constants";
import { store as localStore } from "./utils";

export interface Todo {
  id: string;
  title: string;
  completed: boolean;
}

export interface TodosState {
  todos: Todo[];
  nowShowing: string;
  editingId: string | null;
}

const STORAGE_KEY = "react-todos";

const initialState: TodosState = {
  todos: localStore<Todo>(STORAGE_KEY) as Todo[],
  nowShowing: ALL_TODOS,
  editingId: null,
};

const todosSlice = createSlice({
  name: "todos",
  initialState,
  reducers: {
    addTodo: {
      reducer(state, action: PayloadAction<Todo>) {
        state.todos.push(action.payload);
        localStore(STORAGE_KEY, state.todos);
      },
      prepare(title: string) {
        return {
          payload: {
            id: nanoid(),
            title,
            completed: false,
          } as Todo,
        };
      },
    },
    toggleAllTodos(state, action: PayloadAction<boolean>) {
      state.todos = state.todos.map(todo => ({ ...todo, completed: action.payload }));
      localStore(STORAGE_KEY, state.todos);
    },
    toggleTodo(state, action: PayloadAction<string>) {
      state.todos = state.todos.map(todo =>
        todo.id === action.payload ? { ...todo, completed: !todo.completed } : todo
      );
      localStore(STORAGE_KEY, state.todos);
    },
    destroyTodo(state, action: PayloadAction<string>) {
      state.todos = state.todos.filter(todo => todo.id !== action.payload);
      localStore(STORAGE_KEY, state.todos);
    },
    editTodo(state, action: PayloadAction<string>) {
      state.editingId = action.payload;
    },
    saveTodo(state, action: PayloadAction<{ id: string; title: string }>) {
      state.todos = state.todos.map(todo =>
        todo.id === action.payload.id ? { ...todo, title: action.payload.title } : todo
      );
      state.editingId = null;
      localStore(STORAGE_KEY, state.todos);
    },
    cancelEdit(state) {
      state.editingId = null;
    },
    clearCompletedTodos(state) {
      state.todos = state.todos.filter(todo => !todo.completed);
      localStore(STORAGE_KEY, state.todos);
    },
    setNowShowing(state, action: PayloadAction<string>) {
      state.nowShowing = action.payload;
    },
  },
});

export const {
  addTodo,
  toggleAllTodos,
  toggleTodo,
  destroyTodo,
  editTodo,
