{"command": "write-file", "file_path": "src/store/todosSlice.ts", "content": "import { createSlice, PayloadAction, nanoid } from '@reduxjs/toolkit';
import { Todo } from '../types';

const getInitialTodos = (): Todo[] => {
  try {
    const savedTodos = localStorage.getItem('react-todos');
    return savedTodos ? JSON.parse(savedTodos) : [];
  } catch (error) {
    console.error("Failed to parse todos from localStorage", error);
    return [];
  }
};

const initialState: Todo[] = getInitialTodos();

const todosSlice = createSlice({
  name: 'todos',
  initialState,
  reducers: {
    addTodo: {
      reducer: (state, action: PayloadAction<Todo>) => {
        state.push(action.payload);
      },
      prepare: (title: string) => ({
        payload: {
          id: nanoid(),
          title,
          completed: false,
        },
      }),
    },
    toggleAll(state, action: PayloadAction<boolean>) {
      for (const todo of state) {
        todo.completed = action.payload;
      }
    },
    toggleTodo(state, action: PayloadAction<string>) {
      const todo = state.find(todo => todo.id === action.payload);
      if (todo) {
        todo.completed = !todo.completed;
      }
    },
    saveTodo(state, action: PayloadAction<{ id: string; title: string }>) {
      const todo = state.find(todo => todo.id === action.payload.id);
      if (todo) {
        todo.title = action.payload.title;
      }
    },
    destroyTodo(state, action: PayloadAction<string>) {
      return state.filter(todo => todo.id !== action.payload);
    },
    clearCompleted(state) {
      return state.filter(todo => !todo.completed);
    },
  },
});

export const {
  addTodo,
  toggleAll,
  toggleTodo,
  saveTodo,
  destroyTodo,
  clearCompleted,
} = todosSlice.actions;

export default todosSlice.reducer;
"}

{"command": "write-file", "file_path": "src/store/store.ts", "content": "import { configureStore } from '@reduxjs/toolkit';
import todosReducer from './todosSlice';

const localStorageMiddleware = (store: any) => (next: any) => (action: any) => {
  const result = next(action);
  if (action.type.startsWith('todos/')) {
    const todosState = store.getState().todos;
    try {
      localStorage.setItem('react-todos', JSON.stringify(todosState));
    } catch (error) {
      console.error("Failed to save todos to localStorage", error);
    }
  }
  return result;
};

export const store = configureStore({
  reducer: {
    todos: todosReducer,
  },
  middleware: (getDefaultMiddleware) => getDefaultMiddleware().concat(localStorageMiddleware),
});

export type RootState = ReturnType<typeof store.getState>;
export type AppDispatch = typeof store.dispatch;
"}

{"command": "write-file", "file_path": "src/components/TodoItem.tsx", "content": "import React, { useState, useEffect, useRef } from 'react';
import { useDispatch } from 'react-redux';
import classNames from 'classnames';

import { Todo } from '../types';
import { toggleTodo, saveTodo, destroyTodo } from '../store/todosSlice';
import { ENTER_KEY, ESCAPE_KEY } from '../constants';

interface TodoItemProps {
  todo: Todo;
  isEditing: boolean;
  onStartEdit: () => void;
  onEndEdit: () => void;
}

const TodoItem: React.FC<TodoItemProps> = ({ todo, isEditing, onStartEdit, onEndEdit }) => {
  const dispatch = useDispatch();
  const [editText, setEditText] = useState(todo.title);
  const editFieldRef = useRef<HTMLInputElement>(null);

  const handleToggle = () => {
    dispatch(toggleTodo(todo.id));
  };

  const handleDestroy = () => {
    dispatch(destroyTodo(todo.id));
  };

  const handleEdit = () => {
    onStartEdit();
  };

  const handleSubmit = () => {
    const val = editText.trim();
    if (val) {
      dispatch(saveTodo({ id: todo.id, title: val }));
      setEditText(val);
    } else {
      handleDestroy();
    }
    onEndEdit();
  };

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === ESCAPE_KEY) {
      setEditText(todo.title);
      onEndEdit();
    } else if (event.key === ENTER_KEY) {
      handleSubmit();
    }
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setEditText(event.target.value);
  };

  useEffect(() => {
    if (isEditing) {
      editFieldRef.current?.focus();
    }
  }, [isEditing]);

  useEffect(() => {
    setEditText(todo.title);
  }, [todo.title]);

  return (
    <li
      className={classNames({
        completed: todo.completed,
        editing: isEditing,
      })}
    >
      <div className="view">
        <input
          className="toggle"
          type="checkbox"
          checked={todo.completed}
          onChange={handleToggle}
        />
        <label onDoubleClick={handleEdit}>{todo.title}</label>
        <button className="destroy" onClick={handleDestroy} />
      </div>
      <input
        ref={editFieldRef}
        className="edit"
        value={editText}
        onBlur={handleSubmit}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
      />
    </li>
  );
};

export default React.memo(TodoItem);
"}

{"command": "write-file", "file_path": "src/components/TodoList.tsx", "content": "import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useLocation } from 'react-router-dom';

import TodoItem from './TodoItem';
import { RootState } from '../store/store';
import { toggleAll } from '../store/todosSlice';
import { ACTIVE_TODOS, COMPLETED_TODOS } from '../constants';

const TodoList: React.FC = () => {
  const dispatch = useDispatch();
  const todos = useSelector((state: RootState) => state.todos);
  const location = useLocation();
  const [editingId, setEditingId] = useState<string | null>(null);

  const activeTodoCount = todos.reduce((count, todo) => (todo.completed ? count : count + 1), 0);

  const handleToggleAll = (event: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(toggleAll(event.target.checked));
  };

  const filteredTodos = todos.filter(todo => {
    switch (location.pathname) {
      case '/active':
        return !todo.completed;
      case '/completed':
        return todo.completed;
      default:
        return true;
    }
  });

  if (todos.length === 0) {
    return null;
  }

  return (
    <section className="main">
      <input
        id="toggle-all"
        className="toggle-all"
        type="checkbox"
        onChange={handleToggleAll}
        checked={activeTodoCount === 0}
      />
      <label htmlFor="toggle-all">Mark all as complete</label>
      <ul className="todo-list">
        {filteredTodos.map(todo => (
          <TodoItem
            key={todo.id}
            todo={todo}
            isEditing={editingId === todo.id}
            onStartEdit={() => setEditingId(todo.id)}
            onEndEdit={() => setEditingId(null)}
          />
        ))}
      </ul>
    </section>
  );
};

export default TodoList;
"}

