{"command": "write-file", "file_path": "src/features/todos/TodoItem.tsx", "content": "import React, { useState, useRef, useEffect } from "react";
import classNames from "classnames";
import { useDispatch } from "react-redux";
import { toggleTodo, removeTodo, editTodo } from "./todosSlice";
import { ITodo } from "../../types";

interface TodoItemProps {
  todo: ITodo;
}

const TodoItem: React.FC<TodoItemProps> = ({ todo }) => {
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [editText, setEditText] = useState(todo.title);
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (isEditing && inputRef.current) {
      const node = inputRef.current;
      node.focus();
      node.setSelectionRange(node.value.length, node.value.length);
    }
  }, [isEditing]);

  const handleToggle = () => {
    dispatch(toggleTodo(todo.id));
  };

  const handleDestroy = () => {
    dispatch(removeTodo(todo.id));
  };

  const handleEdit = () => {
    setIsEditing(true);
    setEditText(todo.title);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditText(e.target.value);
  };

  const handleSubmit = () => {
    const text = editText.trim();
    if (text) {
      dispatch(editTodo({ id: todo.id, text }));
    } else {
      dispatch(removeTodo(todo.id));
    }
    setIsEditing(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === "Escape") {
      setEditText(todo.title);
      setIsEditing(false);
    } else if (e.key === "Enter") {
      handleSubmit();
    }
  };

  return (
    <li className={classNames({ completed: todo.completed, editing: isEditing })}>
      {!isEditing ? (
        <div className="view">
          <input className="toggle" type="checkbox" checked={todo.completed} onChange={handleToggle} />
          <label onDoubleClick={handleEdit}>{todo.title}</label>
          <button className="destroy" onClick={handleDestroy} />
        </div>
      ) : (
        <input
          ref={inputRef}
          className="edit"
          value={editText}
          onChange={handleChange}
          onBlur={handleSubmit}
          onKeyDown={handleKeyDown}
        />
      )}
    </li>
  );
};

export default TodoItem;
"}

{"command": "write-file", "file_path": "src/features/todos/TodoList.tsx", "content": "import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { RootState } from "../../types";
import { addTodo, toggleAll } from "./todosSlice";
import { ALL_TODOS, ACTIVE_TODOS, COMPLETED_TODOS } from "../../constants";
import TodoItem from "./TodoItem";

const TodoList: React.FC = () => {
  const dispatch = useDispatch();
  const todos = useSelector((state: RootState) => state.todos.items);
  const filter = useSelector((state: RootState) => state.todos.filter);
  const [newTodo, setNewTodo] = useState("");

  const handleNewChange = (e: React.ChangeEvent<HTMLInputElement>) => setNewTodo(e.target.value);

  const handleNewKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key !== "Enter") return;
    e.preventDefault();
    const title = newTodo.trim();
    if (title) {
      dispatch(addTodo(title));
      setNewTodo("");
    }
  };

  const handleToggleAll = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(toggleAll(e.target.checked));
  };

  const filteredTodos = todos.filter((todo) => {
    switch (filter) {
      case ACTIVE_TODOS:
        return !todo.completed;
      case COMPLETED_TODOS:
        return todo.completed;
      default:
        return true;
    }
  });

  const activeCount = todos.filter((todo) => !todo.completed).length;

  return (
    <>
      <header className="header">
        <h1>todos</h1>
        <input
          className="new-todo"
          placeholder="What needs to be done?"
          value={newTodo}
          onChange={handleNewChange}
          onKeyDown={handleNewKeyDown}
          autoFocus
        />
      </header>
      {todos.length > 0 && (
        <section className="main">
          <input
            id="toggle-all"
            className="toggle-all"
            type="checkbox"
            onChange={handleToggleAll}
            checked={activeCount === 0}
          />
          <label htmlFor="toggle-all">Mark all as complete</label>
          <ul className="todo-list">
            {filteredTodos.map((todo) => (
              <TodoItem key={todo.id} todo={todo} />
            ))}
          </ul>
        </section>
      )}
    </>
  );
};

export default TodoList;
"}

{"command": "write-file", "file_path": "src/features/todos/todosSlice.ts", "content": "import { createSlice, PayloadAction, nanoid } from '@reduxjs/toolkit';
import { ITodo } from '../../types';
import { ALL_TODOS } from '../../constants';

interface TodosState {
  items: ITodo[];
  filter: string;
}

const initialState: TodosState = {
  items: [],
  filter: ALL_TODOS,
};

const todosSlice = createSlice({
  name: 'todos',
  initialState,
  reducers: {
    addTodo: {
      reducer(state, action: PayloadAction<ITodo>) {
        state.items.push(action.payload);
      },
      prepare(title: string) {
        return { payload: { id: nanoid(), title, completed: false } };
      }
    },
    toggleTodo(state, action: PayloadAction<string>) {
      const todo = state.items.find(todo => todo.id === action.payload);
      if (todo) {
        todo.completed = !todo.completed;
      }
    },
    removeTodo(state, action: PayloadAction<string>) {
      state.items = state.items.filter(todo => todo.id !== action.payload);
    },
    editTodo(state, action: PayloadAction<{ id: string; text: string }>) {
      const todo = state.items.find(todo => todo.id === action.payload.id);
      if (todo) {
        todo.title = action.payload.text;
      }
    },
    toggleAll(state, action: PayloadAction<boolean>) {
      state.items.forEach(todo => {
        todo.completed = action.payload;
      });
    },
    clearCompleted(state) {
      state.items = state.items.filter(todo => !todo.completed);
    },
    setFilter(state, action: PayloadAction<string>) {
      state.filter = action.payload;
    }
  }
});

export const { addTodo, toggleTodo, removeTodo, editTodo, toggleAll, clearCompleted, setFilter } = todosSlice.actions;
export default todosSlice.reducer;
"}

