import React from 'react';
import { useDispatch } from 'react-redux';
import { toggleTodo, deleteTodo, editTodo } from './todoSlice';
import { Todo } from '../../types';
import useFocus from '../../hooks/useFocus';
import useEscape from '../../hooks/useEscape';

interface TodoItemProps {
  todo: Todo;
}

const TodoItem: React.FC<TodoItemProps> = ({ todo }) => {
  const [editing, setEditing] = React.useState(false);
  const [editText, setEditText] = React.useState(todo.title);
  const dispatch = useDispatch();
  const editInputRef = useFocus(editing);
  useEscape(() => setEditing(false));

  const handleDoubleClick = () => {
    setEditing(true);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditText(e.target.value);
  };

  const handleBlur = () => {
    if (editing) {
      dispatch(editTodo({
        id: todo.id,
        title: editText.trim()
      }));
      setEditing(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleBlur();
    } else if (e.key === 'Escape') {
      setEditText(todo.title);
      setEditing(false);
    }
  };

  return (
    <li className={`${todo.completed ? 'completed' : ''} ${editing ? 'editing' : ''}`}>
      <div className="view">
        <input className="toggle" type="checkbox" checked={todo.completed} onChange={() => dispatch(toggleTodo(todo.id))} />
        <label onDoubleClick={handleDoubleClick}>{todo.title}</label>
        <button className="destroy" onClick={() => dispatch(deleteTodo(todo.id))}></button>
      </div>
      {editing && (
        <input
          ref={editInputRef}
          className="edit"
          value={editText}
          onBlur={handleBlur}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
        />
      )}
    </li>
  );
};

export default TodoItem;
