import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { clearCompleted, toggleAll } from './todoSlice';
import { selectTodos, selectActiveTodos } from './todoSlice';

const TodoFooter: React.FC = () => {
  const todos = useSelector(selectTodos);
  const activeTodos = useSelector(selectActiveTodos);
  const dispatch = useDispatch();

  const handleClearCompleted = () => {
    dispatch(clearCompleted());
  };

  const handleToggleAll = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(toggleAll(e.target.checked));
  };

  return (
    <footer className="footer" style={{ display: todos.length ? 'block' : 'none' }}>
      <span className="todo-count">
        <strong>{activeTodos.length}</strong> {activeTodos.length === 1 ? 'item' : 'items'} left
      </span>
      <ul className="filters">
        <li>
          <a href="#/">All</a>
        </li>
        <li>
          <a href="#/active">Active</a>
        </li>
        <li>
          <a href="#/completed">Completed</a>
        </li>
      </ul>
      {todos.some(todo => todo.completed) && (
        <button className="clear-completed" onClick={handleClearCompleted}>
          Clear completed
        </button>
      )}
      <input
        id="toggle-all-footer"
        className="toggle-all"
        type="checkbox"
        onChange={handleToggleAll}
        checked={activeTodos.length === 0 && todos.length > 0}
      />
      <label htmlFor="toggle-all-footer">Mark all as complete</label>
    </footer>
  );
};

export default TodoFooter;
