import { useEffect, useRef } from 'react';

const useFocus = (isFocused: boolean): React.RefObject<HTMLInputElement> => {
  const htmlElRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (isFocused && htmlElRef.current) {
      htmlElRef.current.focus();
    }
  }, [isFocused]);

  return htmlElRef;
};

export default useFocus;
