import { useSelector, useDispatch } from 'react-redux';
import { clearCompletedTodos, setFilter, FilterType } from '../todosSlice';
import { RootState } from '../../../store';
import { ALL_TODOS, ACTIVE_TODOS, COMPLETED_TODOS } from '../constants';

const TodoFooter: React.FC = () => {
  const dispatch = useDispatch();
  const activeCount = useSelector(
    (state: RootState) => state.todos.todos.filter((todo) => !todo.completed).length
  );
  const completedCount = useSelector(
    (state: RootState) => state.todos.todos.filter((todo) => todo.completed).length
  );
  const currentFilter = useSelector((state: RootState) => state.todos.filter);

  const handleClearCompleted = () => {
    dispatch(clearCompletedTodos());
  };

  const handleFilterChange = (filter: FilterType) => {
    dispatch(setFilter(filter));
  };

  return (
    <footer className="footer">
      <span className="todo-count">
        <strong>{activeCount}</strong> item{activeCount === 1 ? '' : 's'} left
      </span>
      <ul className="filters">
        <li>
          <a
            href="#/"
            className={currentFilter === ALL_TODOS ? 'selected' : ''}
            onClick={() => handleFilterChange(ALL_TODOS)}
          >
            All
          </a>
        </li>
        <li>
          <a
            href="#/active"
            className={currentFilter === ACTIVE_TODOS ? 'selected' : ''}
            onClick={() => handleFilterChange(ACTIVE_TODOS)}
          >
            Active
          </a>
        </li>
        <li>
          <a
            href="#/completed"
            className={currentFilter === COMPLETED_TODOS ? 'selected' : ''}
            onClick={() => handleFilterChange(COMPLETED_TODOS)}
          >
            Completed
          </a>
        </li>
      </ul>
      {completedCount > 0 && (
        <button className="clear-completed" onClick={handleClearCompleted}>
          Clear completed
        </button>
      )}
    </footer>
  );
};

export default TodoFooter;
"}

{"command": "write-file", "file_path": "src/features/todos/components/TodoItem.tsx", "content": "import React, { useState, useRef, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { toggleTodo, updateTodo, deleteTodo } from '../todosSlice';
import { Todo } from '../types';

interface TodoItemProps {
  todo: Todo;
}

const TodoItem: React.FC<TodoItemProps> = ({ todo }) => {
  const dispatch = useDispatch();
  const [editing, setEditing] = useState(false);
  const [title, setTitle] = useState(todo.title);
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (editing) {
      inputRef.current?.focus();
    }
  }, [editing]);

  const handleToggle = () => {
    dispatch(toggleTodo(todo.id));
  };

  const handleEdit = () => {
    setEditing(true);
    setTitle(todo.title);
  };

  const handleSave = () => {
    if (title.trim() === '') {
      dispatch(deleteTodo(todo.id));
    } else {
      dispatch(updateTodo({ id: todo.id, title: title.trim() }));
    }
    setEditing(false);
  };

  const handleCancel = () => {
    setEditing(false);
  };

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setTitle(event.target.value);
  };

  const handleKeyDown = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === 'Enter') {
      handleSave();
    } else if (event.key === 'Escape') {
      handleCancel();
    }
  };

  const handleDelete = () => {
    dispatch(deleteTodo(todo.id));
  };

  return (
    <li className={editing ? 'editing' : todo.completed ? 'completed' : ''}>
      <div className="view">
        <input
          className="toggle"
          type="checkbox"
          checked={todo.completed}
          onChange={handleToggle}
        />
        <label onDoubleClick={handleEdit}>{todo.title}</label>
        <button className="destroy" onClick={handleDelete} />
      </div>
      {editing && (
        <input
          ref={inputRef}
          className="edit"
          value={title}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          onBlur={handleSave}
        />
      )}
    </li>
  );
};

export default TodoItem;
"}

{"command": "write-file", "file_path": "src/features/todos/Todos.tsx", "content": "import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { toggleAllTodos, selectFilteredTodos } from './todosSlice';
import { RootState } from '../../store';
import TodoItem from './components/TodoItem';
import TodoFooter from './components/TodoFooter';
import AddTodo from './components/AddTodo';

const Todos: React.FC = () => {
  const dispatch = useDispatch();
  const todos = useSelector(selectFilteredTodos);
  const allCompleted = useSelector(
    (state: RootState) => state.todos.todos.length > 0 && state.todos.todos.every((todo) => todo.completed)
  );

  const handleToggleAll = () => {
    dispatch(toggleAllTodos(!allCompleted));
  };

  return (
    <>
      <AddTodo />
      {useSelector((state: RootState) => state.todos.todos.length > 0) && (
        <section className="main">
          <input
            id="toggle-all"
            className="toggle-all"
            type="checkbox"
            checked={allCompleted}
            onChange={handleToggleAll}
          />
          <label htmlFor="toggle-all">Mark all as complete</label>
          <ul className="todo-list">
            {todos.map((todo) => (
              <TodoItem key={todo.id} todo={todo} />
            ))}
          </ul>
        </section>
      )}
      {useSelector((state: RootState) => state.todos.todos.length > 0) && <TodoFooter />}
    </>
  );
};

export default Todos;
"}

{"command": "write-file", "file_path": "src/features/todos/todosSlice.ts", "content": "import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { nanoid } from 'nanoid';
import { Todo, FilterType } from './types';
import { ALL_TODOS, ACTIVE_TODOS, COMPLETED_TODOS } from './constants';

interface TodosState {
  todos: Todo[];
  filter: FilterType;
}

const initialState: TodosState = {
  todos: [],
  filter: ALL_TODOS,
};

const todosSlice = createSlice({
  name: 'todos',
  initialState,
  reducers: {
    addTodo: {
      reducer: (state, action: PayloadAction<Todo>) => {
        state.todos.push(action.payload);
      },
      prepare: (title: string) => ({
        payload: {
          id: nanoid(),
          title,
          completed: false,
        } as Todo,
      }),
    },
    toggleTodo: (state, action: PayloadAction<string>) => {
      const todo = state.todos.find((todo) => todo.id === action.payload);
      if (todo) {
        todo.completed = !todo.completed;
      }
    },
    updateTodo: (state, action: PayloadAction<{ id: string; title: string }>) => {
      const todo = state.todos.find((todo) => todo.id === action.payload.id);
      if (todo) {
        todo.title = action.payload.title;
      }
    },
    deleteTodo: (state, action: PayloadAction<string>) => {
      state.todos = state.todos.filter((todo) => todo.id !== action.payload);
    },
    toggleAllTodos: (state, action: PayloadAction<boolean>) => {
      state.todos = state.todos.map((todo) => ({ ...todo, completed: action.payload }));
    },
    clearCompletedTodos: (state) => {
      state.todos = state.todos.filter((todo) => !todo.completed);
    },
    setFilter: (state, action: PayloadAction<FilterType>) => {
      state.filter = action.payload;
    },
  },
});

export const {
  addTodo,
  toggleTodo,
  updateTodo,
  deleteTodo,
  toggleAllTodos,
  clearCompletedTodos,
  setFilter,
} = todosSlice.actions;

export const selectFilteredTodos = (state: { todos: TodosState }) => {
  const { todos, filter } = state.todos;
  switch (filter) {
    case ACTIVE_TODOS:
      return todos.filter((todo) => !todo.completed);
    case COMPLETED_TODOS:
      return todos.filter((todo) => todo.completed);
    case ALL_TODOS:
    default:
      return todos;
  }
};

export default todosSlice.reducer;
"}

