import classNames from 'classnames';
import { useAppSelector, useAppDispatch } from '../store/hooks';
import { clearCompleted } from '../store/todoSlice';

interface TodoFooterProps {
  nowShowing: string;
  onFilterChange: (filter: string) => void;
}

const TodoFooter: React.FC<TodoFooterProps> = ({ nowShowing, onFilterChange }) => {
  const dispatch = useAppDispatch();
  const todos = useAppSelector(state => state.todos);
  const activeTodoCount = todos.filter(todo => !todo.completed).length;
  const completedCount = todos.length - activeTodoCount;
  const activeTodoWord = activeTodoCount === 1 ? 'item' : 'items';

  return (
    <footer className="footer">
      <span className="todo-count">
        <strong>{activeTodoCount}</strong> {activeTodoWord} left
      </span>
      <ul className="filters">
        <li>
          <a
            href="#/"
            className={classNames({ selected: nowShowing === 'all' })}
            onClick={() => onFilterChange('all')}
          >
            All
          </a>
        </li>
        {' '}
        <li>
          <a
            href="#/active"
            className={classNames({ selected: nowShowing === 'active' })}
            onClick={() => onFilterChange('active')}
          >
            Active
          </a>
        </li>
        {' '}
        <li>
          <a
            href="#/completed"
            className={classNames({ selected: nowShowing === 'completed' })}
            onClick={() => onFilterChange('completed')}
          >
            Completed
          </a>
        </li>
      </ul>
      {completedCount > 0 && (
        <button className="clear-completed" onClick={() => dispatch(clearCompleted())}>
          Clear completed
        </button>
      )}
    </footer>
  );
};

export default TodoFooter;
"}

{"command": "write-file", "file_path": "./src/components/TodoItem.tsx", "content": "import React, { useState, useRef, useEffect } from 'react';
import classNames from 'classnames';
import { useAppDispatch } from '../store/hooks';
import { toggleTodo, deleteTodo, editTodo } from '../store/todoSlice';

interface TodoItemProps {
  todo: { id: string; title: string; completed: boolean };
}

const TodoItem: React.FC<TodoItemProps> = ({ todo }) => {
  const dispatch = useAppDispatch();
  const [editing, setEditing] = useState(false);
  const [editText, setEditText] = useState(todo.title);
  const editFieldRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (editing) {
      editFieldRef.current?.focus();
    }
  }, [editing]);

  const handleSubmit = () => {
    const val = editText.trim();
    if (val) {
      dispatch(editTodo({ id: todo.id, title: val }));
      setEditText(val);
    } else {
      dispatch(deleteTodo(todo.id));
    }
    setEditing(false);
  };

  const handleEdit = () => {
    setEditing(true);
    setEditText(todo.title);
  };

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === 'Escape') {
      setEditText(todo.title);
      setEditing(false);
    } else if (event.key === 'Enter') {
      handleSubmit();
    }
  };

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setEditText(event.target.value);
  };

  return (
    <li
      className={classNames({
        completed: todo.completed,
        editing: editing,
      })}
    >
      <div className="view">
        <input
          className="toggle"
          type="checkbox"
          checked={todo.completed}
          onChange={() => dispatch(toggleTodo(todo.id))}
        />
        <label onDoubleClick={handleEdit}>{todo.title}</label>
        <button className="destroy" onClick={() => dispatch(deleteTodo(todo.id))} />
      </div>
      {editing && (
        <input
          ref={editFieldRef}
          className="edit"
          value={editText}
          onBlur={handleSubmit}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
        />
      )}
    </li>
  );
};

export default TodoItem;
"}

{"command": "write-file", "file_path": "./src/components/TodoApp.tsx", "content": "import React, { useState, useCallback } from 'react';
import TodoItem from './TodoItem';
import TodoFooter from './TodoFooter';
import { useAppSelector, useAppDispatch } from '../store/hooks';
import { addTodo, toggleAll } from '../store/todoSlice';

const TodoApp: React.FC = () => {
  const dispatch = useAppDispatch();
  const todos = useAppSelector((state) => state.todos);
  const [nowShowing, setNowShowing] = useState('all');

  const handleNewTodoKeyDown = useCallback(
    (event: React.KeyboardEvent<HTMLInputElement>) => {
      if (event.key === 'Enter') {
        const input = event.currentTarget;
        const val = input.value.trim();
        if (val) {
          dispatch(addTodo(val));
          input.value = '';
        }
      }
    },
    [dispatch]
  );

  const filteredTodos = todos.filter((todo) => {
    switch (nowShowing) {
      case 'active':
        return !todo.completed;
      case 'completed':
        return todo.completed;
      default:
        return true;
    }
  });

  const activeTodoCount = todos.reduce((accum, todo) => {
    return todo.completed ? accum : accum + 1;
  }, 0);

  return (
    <div>
      <header className="header">
        <h1>todos</h1>
        <input
          className="new-todo"
          placeholder="What needs to be done?"
          onKeyDown={handleNewTodoKeyDown}
          autoFocus={true}
        />
      </header>
      <section className="main">
        <input
          id="toggle-all"
          className="toggle-all"
          type="checkbox"
          onChange={(e) => dispatch(toggleAll(e.target.checked))}
          checked={activeTodoCount === 0}
        />
        <label htmlFor="toggle-all">Mark all as complete</label>
        <ul className="todo-list">
          {filteredTodos.map((todo) => (
            <TodoItem key={todo.id} todo={todo} />
          ))}
        </ul>
      </section>
      <TodoFooter nowShowing={nowShowing} onFilterChange={setNowShowing} />
    </div>
  );
};

export default TodoApp;
"}
